--
-- Estructura de tabla para la tabla `corre`
--

CREATE TABLE IF NOT EXISTS `corre` (
  `idco` int(5) NOT NULL,
  `nomdoc` varchar(50) NOT NULL,
  `folio` varchar(10) NOT NULL,
  `codigo` varchar(15) NOT NULL,
  `hallazgo` varchar(900) NOT NULL,
  `notifica` date NOT NULL,
  `area` varchar(60) NOT NULL,
  `lider` varchar(50) NOT NULL,
  `nomhalla` varchar(50) NOT NULL,
  `nomres` varchar(50) NOT NULL,
  `descrip` varchar(200) NOT NULL,
  `accion` varchar(200) NOT NULL,
  `audito` varchar(50) NOT NULL,
  `fecha` date NOT NULL,
  `tipo` varchar(3) NOT NULL,
  `var1` varchar(10) NOT NULL,
  `var2` varchar(10) NOT NULL,
  `area1` varchar(60) DEFAULT NULL,
  `area2` varchar(60) DEFAULT NULL,
  `area3` varchar(60) DEFAULT NULL,
  `nomres1` varchar(50) DEFAULT NULL,
  `nomres2` varchar(50) DEFAULT NULL,
  `nomres3` varchar(50) DEFAULT NULL,
  `cancel` varchar(3) DEFAULT NULL
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=latin1;

--
-- Volcado de datos para la tabla `corre`
--

INSERT INTO `corre` (`idco`, `nomdoc`, `folio`, `codigo`, `hallazgo`, `notifica`, `area`, `lider`, `nomhalla`, `nomres`, `descrip`, `accion`, `audito`, `fecha`, `tipo`, `var1`, `var2`, `area1`, `area2`, `area3`, `nomres1`, `nomres2`, `nomres3`, `cancel`) VALUES
(1, 'Base de datos  para el pregistro de aspirantes', '1', 'Sin c&Atilde;&s', 'Derivado de la auditoria interna numero 28, se detecto que en el sistema de prerregistro de aspirantes existe un apartado para colocar el origen de los interesados, sin embargo no hay posibilidad de especificar la escuela de procedencia en el caso de que la escuela no se encuentre en el listado de opciones, para generar una adecuada rastreabilidad.', '2018-01-23', 'Promocion Institucional', 'Jorge Parra Tobon', '', 'Virginia Garcia Garcia', '', '', '', '0000-00-00', '2', 'jorge22', 'jparrat', '', '', '', '', '', '', NULL),
(2, 'Formato encuesta de atencion de servi', '2', 'FR-PI-01', 'Derivado de la Auditoria interna numero 28, se detecto en los expedientes de las visitas, platicas y ferias que existen registros con informacion incompleta, especificamente  en los formatos FR-PI-01. Ademas no hay evidencia de que los maestros encargados de los grupo visitantes tengan conocimientos de las reglas que deben seguir para el ingreso y durante la instancia en la UTP, asi como el procedimiento para realizar, felicitaciones, quejas y/o sugerencias.', '2018-01-23', 'Promocion Institucional', 'Jorge Parra Tobon', '', 'Virginia Garcia Garcia', '', '', '', '0000-00-00', '2', 'jorge22', 'jparrat', '', '', '', '', '', '', NULL),
(3, 'Identificacion de riesgos', '3', '', 'Derivado de la auditoria interna numero 28, se detecto que en el formato de identificacion de riesgos, no se contempla las contingencias ambientales, aspecto que afecta de manera importante el desarrollo de las acciones establecidas en su Programa de Trabajo.', '2018-01-23', 'Promocion Institucional', 'Jorge Parra Tobon', '', 'Virginia Garcia Garcia', '', '', '', '0000-00-00', '2', 'jorge22', 'jparrat', '', '', '', '', '', '', NULL),
(6, 'Encuesta de atencion de servicio', '', 'FR-PI-01', 'Derivado de la auditoria interna numero 28, se detecto que el formato FR-PI-01 contiene una escala de medicion limitada que no les permite realizar informes adecuados y cabe senalar que en estos informes se maneja otra escala. Ademas no se especifica una meta de satisfaccion del cliente.', '2018-01-23', 'Promocion Institucional', 'Jorge Parra Tobon', '', 'Virginia Garcia Garcia', '', '', '', '0000-00-00', '2', 'jorge22', 'jparrat', '', '', '', '', '', '', NULL),
(7, 'POA', '', 'Sin codigo', 'Derivado de la auditoria interna numero 28, se detecto que los objetivos declarados en el POA para el cuatrimestre septiembre - diciembre 2017 no se cumpliran debido a la contingencia de septiembre, afectandose visitas, desayunos y ferias.', '2018-01-23', 'PromociÃ³n Institucional', 'Jorge Parra Tobon', '', 'Virginia Garcia Garcia', '', '', '', '0000-00-00', '2', 'jorge22', 'jparrat', '', '', '', '', '', '', NULL);

